INSERT INTO `wp_postmeta` VALUES("2079", "177", "belief_1_heading", "Leadership");
INSERT INTO `wp_postmeta` VALUES("2080", "177", "_belief_1_heading", "field_5bf1bec058a42");
INSERT INTO `wp_postmeta` VALUES("2081", "177", "belief_1_content", "In the early 1990s, the founders of Equius Partners left the Wall Street herd to embrace a new approach to investing based on the recognition that markets and investing science have changed dramatically in the modern era. As one of the first financial planning and investment advisory firms to fully reject traditional active management and adopt a more optimally diversified and structured asset class investing strategy, Equius took the lead and never looked back. ");
INSERT INTO `wp_postmeta` VALUES("2082", "177", "_belief_1_content", "field_5bf1beda58a43");
