INSERT INTO `wp_postmeta` VALUES("2092", "177", "_belief_3_heading", "field_5bf1bf5d58a4a");
INSERT INTO `wp_postmeta` VALUES("2093", "177", "belief_3_content", "Our investment approach is grounded in the work of Eugene Fama, recipient of the 2013 Nobel Prize in Economic Sciences for his work on the efficient market theory and other top academics such as Daniel Kahneman in the area of behavioral finance. Fama, along with his colleague Ken French, also introduced groundbreaking research demonstrating that markets are multidimensional (in terms of risk and return) and that long-term portfolio results can be enhanced with thoughtful diversification among these dimensions.");
