INSERT INTO `wp_postmeta` VALUES("2328", "475", "_ns_content", "field_5bf1bc705b686");
INSERT INTO `wp_postmeta` VALUES("2329", "475", "belief_1_heading", "Leadership");
INSERT INTO `wp_postmeta` VALUES("2330", "475", "_belief_1_heading", "field_5bf1bec058a42");
INSERT INTO `wp_postmeta` VALUES("2331", "475", "belief_1_content", "In the early 1990s, the founders of Equius Partners left the Wall Street herd to embrace a new approach to investing based on the recognition that markets and investing science have changed dramatically in the modern era. As one of the first financial planning and investment advisory firms to fully reject traditional active management and adopt a more optimally diversified and structured asset class investing strategy, Equius took the lead and never looked back. ");
INSERT INTO `wp_postmeta` VALUES("2332", "475", "_belief_1_content", "field_5bf1beda58a43");
INSERT INTO `wp_postmeta` VALUES("2333", "475", "belief_1_image", "93");
INSERT INTO `wp_postmeta` VALUES("2334", "475", "_belief_1_image", "field_5bf1bef458a44");
INSERT INTO `wp_postmeta` VALUES("2335", "475", "belief_2_heading", "Philosophy");
INSERT INTO `wp_postmeta` VALUES("2336", "475", "_belief_2_heading", "field_5bf1bf2e58a46");
INSERT INTO `wp_postmeta` VALUES("2337", "475", "belief_2_content", "We believe that knowledge is the foundation upon which successful portfolios are built and maintained. Not simply a knowledge of investment principles, but a deeper knowledge of who we all are as individuals and investors. Knowledge leads to confidence and confidence fosters discipline when it is needed most. Adherence to these principles inevitably leads to investment success.");
INSERT INTO `wp_postmeta` VALUES("2338", "475", "_belief_2_content", "field_5bf1bf2e58a47");
INSERT INTO `wp_postmeta` VALUES("2339", "475", "belief_2_image", "94");
INSERT INTO `wp_postmeta` VALUES("2340", "475", "_belief_2_image", "field_5bf1bf2e58a48");
INSERT INTO `wp_postmeta` VALUES("2341", "475", "belief_3_heading", "Social");
INSERT INTO `wp_postmeta` VALUES("2342", "475", "_belief_3_heading", "field_5bf1bf5d58a4a");
INSERT INTO `wp_postmeta` VALUES("2343", "475", "belief_3_content", "Our investment approach is grounded in the work of Eugene Fama, recipient of the 2013 Nobel Prize in Economic Sciences for his work on the efficient market theory and other top academics such as Daniel Kahneman in the area of behavioral finance. Fama, along with his colleague Ken French, also introduced groundbreaking research demonstrating that markets are multidimensional (in terms of risk and return) and that long-term portfolio results can be enhanced with thoughtful diversification among these dimensions.");
